/*Table structure for table `ad_article` */

DROP TABLE IF EXISTS `ad_article`;

CREATE TABLE `ad_article` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `title` text NOT NULL COMMENT 'Tiêu đề bài viết',
  `alttitle` varchar(255) DEFAULT NULL COMMENT 'Tiêu đề rút gọn',
  `header` longtext COMMENT 'Trích yếu',
  `body` longtext COMMENT 'Nội dung bài viết trên web',
  `image_path` varchar(255) DEFAULT NULL COMMENT 'Đường dẫn ảnh đại diện',
  `attributes` bigint(20) DEFAULT NULL COMMENT 'Thuộc tính của bài viết: khuyến mại, ',
  `hit_count` bigint(20) DEFAULT '0' COMMENT 'Số lượt truy cập',
  `priority` bigint(20) NOT NULL COMMENT 'Độ ưu tiên hiển thị',
  `published_time` datetime DEFAULT NULL COMMENT 'Thời gian xuất bản',
  `expiredate_time` datetime DEFAULT NULL COMMENT 'Thời gian kết thúc bản tin',
  `meta` varchar(255) DEFAULT NULL COMMENT 'Nội dung meta',
  `keywords` varchar(255) DEFAULT NULL COMMENT 'Nội dung keywords',
  `author` varchar(255) DEFAULT NULL COMMENT 'Tác giả',
  `other_link` longtext COMMENT 'Các link liên quan',
  `is_active` bigint(20) NOT NULL DEFAULT '0' COMMENT 'Trạng thái hiển thị (-1: Bản nháp, 0- Chờ duyệt, 1- Đã duyệt, 2- Đã đăng)',
  `is_hot` tinyint(1) NOT NULL DEFAULT '0' COMMENT 'Hiển thị trên trang chủ (0- ko hiển thị, 1- hiển thị)',
  `lang` varchar(10) NOT NULL DEFAULT 'en' COMMENT 'Đa ngôn ngữ',
  `slug` varchar(255) NOT NULL DEFAULT '123123-12312' COMMENT 'chuyển đổi url',
  `category_id` bigint(20) DEFAULT NULL COMMENT 'Id của danh mục tin tức',
  `created_by` bigint(20) DEFAULT NULL,
  `updated_by` bigint(20) DEFAULT NULL,
  `created_at` datetime NOT NULL,
  `updated_at` datetime NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `slug` (`slug`),
  KEY `category_id_idx` (`category_id`),
  KEY `created_by_idx` (`created_by`),
  KEY `updated_by_idx` (`updated_by`)
) ENGINE=MyISAM AUTO_INCREMENT=251 DEFAULT CHARSET=utf8;

/*Data for the table `ad_article` */

insert  into `ad_article`(`id`,`title`,`alttitle`,`header`,`body`,`image_path`,`attributes`,`hit_count`,`priority`,`published_time`,`expiredate_time`,`meta`,`keywords`,`author`,`other_link`,`is_active`,`is_hot`,`lang`,`slug`,`category_id`,`created_by`,`updated_by`,`created_at`,`updated_at`) values (225,'VIETNAM RATTAN CHAIRS - RATTAN FURNITURE FOR EXPORT','','The Vietnam bamboo rattan furniture is designed simple but many diverse models. Rattan furniture is not only popular with domestic consumers but also in many Asian and American countries.','<p>Today, indoor furniture such as tables, chairs and cabinets are used by families instead of wooden furniture.&nbsp;<strong>Vietnam</strong> <strong>Rattan furniture is</strong>&nbsp;not only used as a room item, but also as a decoration that is close to nature.</p>\r\n\r\n<p>The Vietnam bamboo rattan&nbsp;furniture is designed simple but many diverse models.&nbsp;<strong>Rattan furniture</strong>&nbsp;is not only popular with domestic consumers but also in many Asian and American countries.</p>\r\n\r\n<p>If you are interested in exported rattan furniture or you are interested in&nbsp;<strong>relaxing rattan chairs</strong>&nbsp;one of the furniture.&nbsp;You can follow the article below.</p>\r\n\r\n<h3><strong>Vietnam Bamboo furniture - rattan furniture for export</strong></h3>\r\n\r\n<p><strong>Bamboo furniture</strong>&nbsp;is one of the items designed to be used most in the living room.&nbsp;With a simple design, beautiful, luxurious bamboo tables and chairs always bring satisfaction to users.</p>\r\n\r\n<p>Vietnam Bamboo tables and chairs are usually placed right in the middle of the living room for reception, also where family members sit down to talk.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ban%20ghe%20may%20tre.jpg\" style=\"height:500px; width:500px\" /></p>\r\n\r\n<h3><strong>Relaxing rattan chairs - symbols of emotions</strong></h3>\r\n\r\n<p><a href=\"https://translate.googleusercontent.com/translate_c?depth=1&amp;hl=vi&amp;rurl=translate.google.com&amp;sl=vi&amp;sp=nmt4&amp;tl=en&amp;u=https://denmaytre.vn/san-pham&amp;xid=17259,15700022,15700186,15700190,15700256,15700259,15700262,15700265,15700271,15700280,15700283&amp;usg=ALkJrhgsz-9n6rHfm_JlPhl0G6eC2q5gAw\">Relaxing rattan chair</a> is one of the items with many different designs such as long rattan chair, round rattan chair, double rattan chair one ... but they all have a common use, helping the owner to sitting or lying down to relax, reducing strees after stressful and tired working sessions.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/2(2).jpg\" style=\"height:624px; width:500px\" /></p>\r\n\r\n<h3><strong>Advantages of exported rattan furniture</strong></h3>\r\n\r\n<p>- Highly aesthetic and vivid, easy to clean favorite items clean.</p>\r\n\r\n<p>- Durability: Bamboo chairs have longevity and durability, can reach durability equal to the life of a person.</p>\r\n\r\n<p>- Close to the living environment.&nbsp;When you use bamboo and rattan utensils, we contribute to maintain and preserve the green environment, for a green future.</p>\r\n\r\n<p>- Cheap price, compatible with small family economy, because of its light characteristics, easy to move, extremely suitable for many living spaces, helping the house become airy, expanding space.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/92.jpg\" style=\"height:375px; width:500px\" /></p>\r\n\r\n<h3><strong>How to preserve bamboo tables and chairs and rattan relaxation</strong></h3>\r\n\r\n<ul>\r\n	<li>Should be exposed to the sun 2 days after buying to ultraviolet rays can kill bacteria hidden inside rattan.</li>\r\n	<li>Can disinfect spray to kill mold, termites before use.&nbsp;Leave after 24 hours since spraying.</li>\r\n	<li>After 1 year of use, it should be painted once again so the product has good durability.</li>\r\n</ul>\r\n\r\n<p>Hopefully, the rattan and bamboo products available at&nbsp;<strong>Glovimex Bamboo and Rattan</strong>&nbsp;will help you make the right choices for green living space in your beloved home.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/107.jpg\" style=\"height:332px; width:500px\" /></p>','/cd/6d/dc/5dc294ac44bda.png',NULL,0,0,'2019-11-06 00:00:00',NULL,'The Vietnam bamboo rattan furniture is designed simple but many diverse models. Rattan furniture is not only popular with domestic consumers but also in many Asian and American countries.','Vietnam Rattan Furniture, bamboo rattan furniture','','',2,1,'en','vietnam-rattan-chairs-rattan-furniture-for-export',5,1,22,'2019-11-06 16:24:36','2020-04-07 21:28:05'),(226,'RATTAN FURNITURE PRODUCTION AND PROCESSING','','The rattan is supple and has a smooth surface that gives a gentle, elegant impression that cannot be made from machines or other materials. In addition, natural rattan furniture products are transferred by diligent and loving craftsmanship in Vietnam through their products.','<h2 style=\"font-style:italic\">&nbsp;</h2>\r\n\r\n<p style=\"margin-left:7.1pt\">The strength of rattan furniture is light and firm, close to humans and easy to repair. With softness and flexibility, it is possible to design freely with complex and sophisticated curves. The rattan is supple and has a smooth surface that gives a gentle, elegant impression that cannot be made from machines or other materials. In addition, natural <strong>rattan furniture products</strong> are transferred by diligent and loving craftsmanship in Vietnam through their products.</p>\r\n\r\n<p style=\"margin-left:7.1pt\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:7.1pt\"><img alt=\"\" src=\"/uploads/ckfinder/images/Picture1(2).png\" style=\"height:703px; width:625px\" /><br />\r\n&nbsp;</p>\r\n\r\n<h3 style=\"margin-left: 7.1pt;\"><strong>&nbsp; &nbsp; &nbsp;Rattan furniture production process</strong></h3>\r\n\r\n<ol>\r\n</ol>\r\n\r\n<p style=\"margin-left:7.1pt\"><em><strong>&nbsp; &nbsp; &nbsp; 1. Preparing material</strong></em></p>\r\n\r\n<ol>\r\n</ol>\r\n\r\n<p style=\"margin-left:25.1pt\">Raw rattan is processed through initial stages of drying, bending, rubbing,in order to rid them of insects and larvae, reduce fungal disease and increase durability.</p>\r\n\r\n<p style=\"margin-left:25.1pt\">Selecting rattan of different thickness, stiffness, color ... and cut to size of rattan furniture structure parts.</p>\r\n\r\n<p style=\"margin-left:25.1pt\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"/uploads/ckfinder/images/1.png\" style=\"height:446px; width:500px\" /></p>\r\n\r\n<p><em><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2. Straighten the rattan</strong></em></p>\r\n\r\n<p style=\"margin-left:25.1pt\">Rattan are natural material, so tree-trunk are always curved and twisted naturally. Therefore,<strong> </strong><strong>in rattan furniture production process</strong>, we use levers, machines to straighten the rattan.</p>\r\n\r\n<p style=\"margin-left:25.1pt\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"/uploads/ckfinder/images/2(1).png\" style=\"height:441px; width:500px\" /></p>\r\n\r\n<p>&nbsp; &nbsp; <strong><em>&nbsp;</em></strong></p>\r\n\r\n<p><strong><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;3. Bending Rattan</em></strong></p>\r\n\r\n<p style=\"margin-left:25.1pt\">Rattans are steamed in a boiler to make them more soft and easier to curve. Then the rattan is bent according to the shape of each component of the furniture.</p>\r\n\r\n<p style=\"margin-left:25.1pt\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"/uploads/ckfinder/images/3(1).png\" style=\"height:416px; width:500px\" /></p>\r\n\r\n<p>&nbsp; &nbsp;<strong><em> &nbsp;</em></strong></p>\r\n\r\n<p><strong><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;4. Positioning rattan in mold</em></strong></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;The bending chair components are finished surface treatment with sandpaper the scratch location.</p>\r\n\r\n<p style=\"margin-left:25.1pt\">Positioning the rattan that has been curved into the mold and stored in a stable room to ensure the standard moisture.</p>\r\n\r\n<p style=\"margin-left:25.1pt\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"/uploads/ckfinder/images/4(2).png\" style=\"height:457px; width:500px\" /></p>\r\n\r\n<p><em><strong>&nbsp; &nbsp; &nbsp; </strong></em></p>\r\n\r\n<p><em><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;5. Framing the furniture</strong></em></p>\r\n\r\n<p style=\"margin-left:25.1pt\">The components are assembled by stainless steel screws. The seat frame system is adjusted manually to fit the best model.</p>\r\n\r\n<p style=\"margin-left:25.1pt\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"/uploads/ckfinder/images/5.png\" style=\"height:427px; width:500px\" /></p>\r\n\r\n<p>&nbsp; &nbsp;<strong><em> &nbsp; 6. Winding and Braiding Rattan</em></strong></p>\r\n\r\n<p style=\"margin-left:25.1pt\">Braiding is carried out carefully by hand in line with the framework. Using dampened, softened rattan, subtle curves and textures are produced while assuming a dry state.&nbsp;<strong>Rattan furniture</strong> are hand-tied with joints or decorative designs. Using damp rattan fibers creates elasticity and after drying will be delicate lines.</p>\r\n\r\n<p style=\"margin-left:25.1pt\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"/uploads/ckfinder/images/6.png\" style=\"height:415px; width:500px\" /></p>\r\n\r\n<p>&nbsp; <strong><em>&nbsp; &nbsp; 7. Finishing</em></strong></p>\r\n\r\n<p style=\"margin-left:25.1pt\">Rattan furniture is fully sanded and the remaining surface treated. Rattan production are painted to protect surface of furniture.</p>\r\n\r\n<p style=\"margin-left:25.1pt\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <img alt=\"\" src=\"/uploads/ckfinder/images/7(1).png\" style=\"height:486px; width:553px\" /></p>\r\n\r\n<p style=\"margin-left:25.1pt\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:25.1pt\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:25.1pt\">-----------------------------------------------------------------------------</p>\r\n\r\n<p><strong>We are here to match your requirement. Do not hesitate to contact us for the best price!</strong></p>\r\n\r\n<p><strong>GLOVIMEX CO.,LTD</strong></p>\r\n\r\n<p><strong>ADD: 18/67&nbsp;Chua Lang Street, Dong Da District, Ha Noi, Viet Nam</strong></p>\r\n\r\n<p><strong>EMAIL: Sale@glovimex.com</strong></p>\r\n\r\n<p><strong>HOTLINE: +84 906246584 (WHATSAPP/VIBER/ZALO)&nbsp;OR WECHAT: JADE_THUY</strong></p>\r\n\r\n<p style=\"margin-left:25.1pt\">&nbsp;</p>','/00/f8/80/5dc3cacd197ce.png',NULL,0,0,'2019-11-07 00:00:00',NULL,'Natural rattan furniture products are transferred by diligent and loving craftsmanship in Vietnam through their products.','Rattan furniture, rattan furniture production process, Rattan furniture in Vietnam','','',2,0,'en','rattan-furniture-production-and-processing',5,30,30,'2019-11-07 14:38:04','2019-11-07 14:48:01'),(221,'HOW TO MAKE BAMBOO AND RATTAN ALWAYS BEAUTIFUL AND DURABLE?','','There are some points which show you how to make bamboo rattan always beautiful and durable:\r\nWhen using rattan furniture, avoid rain and water. Or you can use rattan products in air-conditioned, dry rooms','<p style=\"text-align:justify\">Due to the erratic hot and humid climate in Vietnam, bamboo and rattan product lines if not processed to the right standards will lead to mold and termite status.&nbsp;This will affect the longevity, as well as the rapid degradation of rattan furniture.</p>\r\n\r\n<p style=\"text-align:justify\">There are some points which show you how to make bamboo rattan always beautiful and durable:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">When using rattan furniture, avoid rain and water.&nbsp;Or you can use rattan products in air-conditioned, dry rooms.</li>\r\n	<li style=\"text-align:justify\">Want durable rattan furniture does not expose them to direct sunlight.&nbsp;Because the temperature is too high, it will make the wood paint on bamboo products easy to fade, losing their natural beauty.</li>\r\n	<li style=\"text-align:justify\">Absolutely do not pour chemicals on rattan products.&nbsp;If you accidentally miss ink on the seats, quickly wipe it off with a cloth.</li>\r\n</ul>\r\n\r\n<h3 style=\"text-align:justify\"><em>How to keep bamboo rattan furniture beautiful</em></h3>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">To prevent rattan and bamboo furniture from mold, or termites, spray disinfectant.&nbsp;If the new rattan furniture is bought, then it should be dried from 1 sun to 2 sun to dry, bactericidal products.<img alt=\"\" src=\"/uploads/ckfinder/images/Presentation3(3).jpg\" style=\"height:500px; width:800px\" /></li>\r\n	<li style=\"text-align:justify\">Do not allow direct contact with water and chemicals.&nbsp;It is best to use a sheet of glass to cover the tables and chairs.</li>\r\n	<li style=\"text-align:justify\">For rattan furniture over 1 year to make furniture always beautiful, you should paint them again.&nbsp;Can use glossy paint if you want to keep the old color.&nbsp;Or use oil paint or PU paint if you want your furniture to &quot;wear a new coat&quot;.&nbsp;The main paint color will help protect the surface of the product, avoiding termites.</li>\r\n	<li style=\"text-align:justify\">Clean rattan and bamboo articles often with a small broom, soft cloth, or a vacuum cleaner.</li>\r\n</ul>','/21/4a/a2/5dbf99ed68bc4.png',NULL,0,0,'2019-10-21 00:00:00',NULL,'Due to the erratic hot and humid climate in Vietnam, bamboo and rattan product lines if not processed to the right standards will lead to mold and termite status. This will affect the longevity, as well as the rapid degradation of rattan furniture.','rattan furniture, prevent rattan and bamboo furniture','','',2,1,'en','how-to-make-bamboo-and-rattan-always-beautiful-and-durable',5,1,30,'2019-10-21 15:37:30','2019-11-04 10:24:29'),(222,'OUTSTANDING ADVANTAGES OF BAMBOO AND RATTAN PRODUCTS','','Most joints are very flat, not rough, rough. High-grade rattan furniture is highly elastic, and withstands extremely large loads. Therefore, most items made from rattan materials always bring comfort and comfort to users.','<h3>1.<em>Lightweight, sleek, aesthetic</em></h3>\r\n\r\n<p>The outstanding advantage of bamboo and rattan products is their light durability and smooth surface.</p>\r\n\r\n<p>Most joints are very flat, not rough, rough.&nbsp;High-grade rattan furniture is highly elastic, and withstands extremely large loads.&nbsp;Therefore, most items made from rattan materials always bring comfort and comfort to users.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/Picture2.png\" style=\"height:662px; width:625px\" /></p>\r\n\r\n<h3>2.<em>Diverse models</em></h3>\r\n\r\n<p>Still know that rattan furniture is a natural material line.&nbsp;Therefore, they are easy to manipulate, so the designs on the market are always diverse in style and offer many choices for buyers.</p>\r\n\r\n<p>Moreover, most of the furniture made from rattan and bamboo easily combine with many other furniture accessories, suitable for all design styles to bring a closer, more luxurious living space.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/5-min(1).png\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<h3>3.<em>Rattan and rattan have very high durability</em></h3>\r\n\r\n<p>The rattan furniture, bamboo chairs or bamboo beds ... very easy to get termites, mold and moisture if meeting water.&nbsp;However, in a dry, clean environment, bamboo and rattan interior products will always be more durable from other materials.</p>\r\n\r\n<p>And rattan is a product that is considered to have good durability, high aesthetics over time.</p>\r\n\r\n<h3>4.<em>Bamboo and rattan are easy to clean</em></h3>\r\n\r\n<p>Usually you will always be afraid to clean the house, to clean the family&#39;s furniture, right?&nbsp;However, rattan furniture is the perfect choice because they easily clean dirt.</p>\r\n\r\n<h3><em>5.</em><em>Environmental friendliness</em></h3>\r\n\r\n<p>Rattan furniture is a product of environmental friendliness, contributing to a green and airy future life.<img alt=\"\" src=\"/uploads/ckfinder/images/Picture3.png\" style=\"height:529px; width:600px\" /></p>','/12/7a/a1/5dbf9ccb4df08.png',NULL,0,200,'2019-10-21 00:00:00',NULL,'Most joints are very flat, not rough, rough. High-grade rattan furniture is highly elastic, and withstands extremely large loads. Therefore, most items made from rattan materials always bring comfort and comfort to users.','advantage of bamboo and rattan products, rattan and bamboo easily combine with many other furniture, bamboo and rattan interior products will always be more durable from other materials.','','',2,1,'en','outstanding-advantages-of-bamboo-and-rattan-products',5,1,30,'2019-10-21 15:53:59','2019-11-04 10:38:39'),(220,'HOW TO CLEAN & PRESERVE RATTAN BAMBOO PRODUCTS BEST','','Bamboo and rattan products are handicrafts that are favored by many Vietnamese families because of their safety, environmental friendliness, and especially the much cheaper cost than the products manufactured by other materials. However, they are quite susceptible to mold and termites over time if not properly stored. Here are some ways to preserve rattan products to help you keep your favorite products more durable.','<p style=\"text-align:justify\"><a href=\"https://translate.googleusercontent.com/translate_c?depth=1&amp;hl=vi&amp;rurl=translate.google.com&amp;sl=vi&amp;sp=nmt4&amp;tl=en&amp;u=https://denmaytre.vn/danh-muc/den-may-tre&amp;xid=17259,15700022,15700186,15700190,15700256,15700259,15700262,15700265&amp;usg=ALkJrhisWxsgtJNsRAZ7UueSLavSOP0KTw\"><strong>Bamboo and rattan</strong></a>&nbsp;products are&nbsp;<strong>handicrafts that</strong>&nbsp;are favored by many Vietnamese families because of their safety, environmental friendliness, and especially the much cheaper cost than the products manufactured by other materials.&nbsp;However, they are quite susceptible to mold and termites over time if not properly stored.&nbsp;Here are some ways to&nbsp;<strong>preserve rattan products to</strong>&nbsp;help you keep your favorite products more durable.</p>\r\n\r\n<p style=\"text-align:justify\">How to clean and preserve bamboo and rattan products since they were purchased.</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">From the moment you buy it, you can use the sun exposure to&nbsp;preserve bamboo and rattan products&nbsp;is the simplest way that brings immediate effect.&nbsp;Immediately after buying, you should expose the product to the sun for 1-2 days for ultraviolet rays in the sun to kill the mold bacteria hidden inside the product.&nbsp;This method is both fast and effective.<img alt=\"\" src=\"/uploads/ckfinder/images/Presentation3(3).jpg\" style=\"height:500px; width:800px\" /></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Using disinfectant: This method takes longer than the above method but brings many benefits in the long run.&nbsp;However, it is required to use disinfectants that are not harmful to users, while spraying, remember to keep aerosols in well-ventilated places such as yards, gardens ... spacious and airy places.&nbsp;After finishing the sterilization process for the product, the next step you can wrap the product in a sealed plastic bag and incubate for 1-2 days to absorb evenly the drug, helping to bring anti-fungal effect mold longer.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>How to preserve used rattan and bamboo articles</strong></h3>\r\n\r\n<p style=\"text-align:justify\">To&nbsp;<a href=\"https://translate.googleusercontent.com/translate_c?depth=1&amp;hl=vi&amp;rurl=translate.google.com&amp;sl=vi&amp;sp=nmt4&amp;tl=en&amp;u=https://denmaytre.vn/bao-quan-do-may-tre-dan&amp;xid=17259,15700022,15700186,15700190,15700256,15700259,15700262,15700265&amp;usg=ALkJrhhVXVX9Uo_I8jZgMiz-wz_8nqaRNQ\">preserve used bamboo and rattan</a>&nbsp;products that have moldy, termite conditions, treat them by the following methods.</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Use mineral mud and soak the product for one day.&nbsp;Then take it to wash thoroughly and dry in the sun to make sure, when you use this way the mold phenomenon, termite will be no more.</li>\r\n	<li style=\"text-align:justify\">If there is no mineral mud, it is possible to soak into ponds and lakes where the mud is stored and follow the above steps for the same result.</li>\r\n	<li style=\"text-align:justify\">Mix a little vinegar of white vinegar and white wine in water, soak&nbsp;in rattan products&nbsp;for a day, then go to the sun in the sun until it is completely dry and you can use again completely.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">After applying the methods of&nbsp;preserving rattan&nbsp;in these ways, you can bring your product to paint on the surface to increase the aesthetics as well as the durability, minimizing the status of mushrooms. , weevil on the product.</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">To&nbsp;clean rattan products&nbsp;, make sure to clean gently, avoid scratching the unsightly product by using a soft cloth soaked in water or a small broom, do not use chemicals. Strong detergents have a serious impact on longevity, so use mild detergent to clean.&nbsp;In addition, you can manually paint the ball after 01 year of use to improve the durability of the product.</li>\r\n	<li style=\"text-align:justify\">Using a soft brush is an effective way to clean up dirt inside small slats, especially for&nbsp;rattan furniture&nbsp;.&nbsp;This may take some time, but make sure your&nbsp;rattan&nbsp;is clean for a long time.<img alt=\"\" src=\"/uploads/ckfinder/images/Picture1(1).png\" style=\"height:703px; width:703px\" /></li>\r\n</ul>','/4b/8e/e4/5daeabd06bda2.jpg',NULL,0,0,'2019-10-21 00:00:00',NULL,'To clean rattan products , make sure to clean gently, avoid scratching the unsightly product by using a soft cloth soaked in water or a small broom, do not use chemicals. Strong detergents have a serious impact on longevity, so use mild detergent to clean','bamboo rattan products, how to clean bamboo products, clean bamboo lamoshade','','',2,1,'en','how-to-clean-preserve-rattan-bamboo-products-best',5,1,30,'2019-10-21 15:28:33','2019-10-22 14:12:16'),(223,'THE HOT TREND LAMPSHADES OF RATTAN AND BAMBOO MADE BY VIETNAMESE ARTISAN','','If the expensive crystal lamp are typical for modern and luxurious rooms, here is a sample of the lampshades with elegant style, bringing a sense of closeness to nature . Those are the lampshades made from rattan and bamboo material below. They are items that are expected to explode in the coming years.','<p>If the expensive crystal lamp are typical for modern and luxurious rooms, here is a sample of the lampshades with elegant style, bringing a sense of closeness to nature . Those are the lampshades made from rattan and bamboo material below. They are items that are expected to explode in the coming years. The reason is because the natural design style, in a slightly rustic direction, close to nature is increasingly being chosen by many families in the family interior design.</p>\r\n\r\n<p style=\"text-align:center\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"/uploads/ckfinder/images/14.png\" style=\"height:667px; width:600px\" /></p>\r\n\r\n<p>The lampshades are made of rattan and bamboo, bringing a simple and rustic beauty to each family&#39;s living space.</p>\r\n\r\n<p style=\"text-align:center\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"/uploads/ckfinder/images/7.png\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p>One point you need to keep in mind when choosing this lamp is the interior style of the house. Because not all interior styles can be combined, harmoniously combined with bamboo and rattan lampshades.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/uploads/ckfinder/images/8_optimized.png\" style=\"height:487px; width:500px\" /></p>\r\n\r\n<p>Country style, vintage or retro will be the most suitable suggestion to combine with this type of lampshades.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/uploads/ckfinder/images/6-min.png\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p>Although it is quite new, there are many different lampshades for you to choose for your family living space.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/uploads/ckfinder/images/10-min.png\" style=\"height:445px; width:600px\" /></p>\r\n\r\n<p>You can use this simple, rustic style for a lot of different spaces in the family.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/uploads/ckfinder/images/3.png\" style=\"height:630px; width:600px\" /></p>\r\n\r\n<p>The dining corner of the family suddenly became much more prominent with the presence of a lampshades made of rattan and bamboo.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/uploads/ckfinder/images/5-min.png\" style=\"height:500px; width:500px\" /></p>\r\n\r\n<p>The combination of decorative lights and interiors brings a strange attraction to the room.</p>','/42/7f/f4/5daf1e6c43bc4.png',NULL,0,0,'2019-10-21 00:00:00',NULL,'If the expensive crystal lamp are typical for modern and luxurious rooms, here is a sample of the lampshades with elegant style, bringing a sense of closeness to natur','Vietnam bamboo rattan lampshade,  rattan lampshade, bamboo lampshade','','',2,1,'en','the-hot-trend-lampshades-of-rattan-and-bamboo-made-by-vietnamese-artisan-are-expected-in-the-coming-years',5,1,22,'2019-10-21 16:44:48','2019-10-22 22:21:57'),(224,'1. WHAT IS CEMENT TILE?','','Encaustic Cement tile is a very popular material in the process of housing construction and construction. So what is a cement tile? What are the characteristics of cement tiles? What is the production process and application of cement tiles? The following article will give you satisfactory answers.','<p style=\"text-align:justify\">Encaustic Cement tile is a very popular material in the process of housing construction and construction. So what is a cement tile? What are the characteristics of cement tiles? What is the production process and application of cement tiles? The following article will give you satisfactory answers.</p>\r\n\r\n<p style=\"text-align:justify\">1. What is Cement tile?</p>\r\n\r\n<p style=\"text-align:justify\">Encaustic&nbsp;cement tile or cement tile is a type of tile being used a lot in daily life.</p>\r\n\r\n<p style=\"text-align:justify\">The cement tiles have a variety of colors and designs, and the production process does not pollute the environment so it is widely used.</p>\r\n\r\n<p style=\"text-align:justify\">2. Characteristics of cement tiles</p>\r\n\r\n<p style=\"text-align:justify\">Manufacturing materials: sand, cement, stone powder, pigments, additives. Brick was born under the perfect workmanship of craftsmen and the help of specialized hydraulic compressors.</p>\r\n\r\n<p style=\"text-align:justify\">What is the shape and size of the cement tiles? Size of brick (cm):</p>\r\n\r\n<p style=\"text-align:justify\">200*200*16&nbsp;(mm)</p>\r\n\r\n<p style=\"text-align:justify\">150*150*16 (mm)</p>\r\n\r\n<p style=\"text-align:justify\">140*140*16 (mm)</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"/uploads/ckfinder/images/Picture4.png\" style=\"height:488px; width:800px\" /></p>\r\n\r\n<p style=\"text-align:justify\">This type of cement tile is often decorated with simple patterns such as flowers, rhombus, triangle, circle, butterfly shape, cake, ... The color of cotton is often quite showy and striking.</p>\r\n\r\n<p style=\"text-align:justify\">3 Advantages - disadvantages of cement tile</p>\r\n\r\n<p style=\"text-align:justify\">3.1. What are the advantages of cement tile?</p>\r\n\r\n<p style=\"text-align:justify\">Appearing from the early 19th century , it is no coincidence that cement tiles still retain the &quot;top&quot; position in the decorative materials industry.</p>\r\n\r\n<p style=\"text-align:justify\">Cement tile has high durability</p>\r\n\r\n<p style=\"text-align:justify\">Due to the process of hydraulic compression with high pressure, this tile is very strong and firm. The cement tile can withstand the rigors of the surrounding environment. Very rarely cracking due to normal human effects.</p>\r\n\r\n<p style=\"text-align:justify\">Fire retardant, good insulation</p>\r\n\r\n<p style=\"text-align:justify\">When using cement tile for your project, you will feel more secure about fire and explosion. The components that make cotton the encaustic cement tile such as sand, stone powder, cement are non-flammable and optimally insulated</p>\r\n\r\n<p style=\"text-align:justify\">Production and construction costs are quite cheap</p>\r\n\r\n<p style=\"text-align:justify\">Compared with natural stone, the production and construction costs of cement tile will be much more economical. The material to create the cement tile is simple and easy to find.</p>\r\n\r\n<p style=\"text-align:justify\">Highly aesthetic, diverse in color and design</p>\r\n\r\n<p style=\"text-align:justify\">You can easily select the type of colored cement tile and pattern suitable for the space of the construction work. Moreover, the combination of cement tile for floors and walls also brings quite interesting experience.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"/uploads/ckfinder/images/18.png\" style=\"height:593px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:justify\">Easy to replace when damage occurs</p>\r\n\r\n<p style=\"text-align:justify\">In the case of chipped or faded tiles, investors can easily buy the same type of cement tile for replacement.</p>\r\n\r\n<p style=\"text-align:justify\">High safety, environmentally friendly</p>\r\n\r\n<p style=\"text-align:justify\">The building materials will usually have a certain amount of radioactivity, it can directly affect the air and human health. However, the cement tiles are an exception.</p>\r\n\r\n<p style=\"text-align:justify\">Easy to clean, hygienic</p>\r\n\r\n<p style=\"text-align:justify\">Without using a lot of chemicals, your cleaning water can still clean the tiles quickly and cleanly</p>','/fa/ff/ff/5dbf9d59f0a3a.png',NULL,0,0,'2019-10-21 00:00:00',NULL,'','Cement tile, encaustic cement tile','','',2,1,'en','1-what-is-cement-tile',5,1,30,'2019-10-21 17:10:07','2019-11-04 10:39:06'),(227,'THE WAYS TO USE BAMBOO RATTAN OR SEAGRASS PRODUCT MADE IN VIETNAM','','ou are familiar with the furniture made from rattan bamboo or seagrass material such as tea tables and chairs, but maybe you missed some other uses of home decoration accessories made from this material.  Have you bought yourself seagrass storage baskets or bamboo lampshades made in Vietnam','<p>You are familiar with the furniture made from rattan bamboo or seagrass material such as tea tables and chairs, but maybe you missed some other uses of home decoration accessories made from this material. &nbsp;Have you bought yourself seagrass storage baskets or bamboo lampshades made in Vietnam?&nbsp;Perhaps you will find some great ideas for your home with rattan material from the suggestions below:</p>\r\n\r\n<p><strong>1. Used for storage</strong></p>\r\n\r\n<p>Large and small bamboo baskets can serve the purpose of storing your daily household items.&nbsp;With large size baskets, you can use it to store clothes, toys, or vegetables in the kitchen.&nbsp;What is even better and even better is that you can use a small basket to hold items of the same size as jewelry, keys, etc...</p>\r\n\r\n<p><img a